// import { useState } from "react";
import useLocalStorageState from "use-local-storage-state";

import GlobalStyle from "./styles";
import Header from "./components/Header/index";
import About from "./components/About/index";
import Projects from "./components/Projects";
import Contact from "./components/Contact";
import Form from "./components/Form";
import CommentList from "./components/Comment/CommentList";
import Footer from "./components/Footer";

import ImageAlienInvasion from "./assets/AlienInvasion.svg";

function App() {
  const [comments, setComments] = useLocalStorageState("comments", {
    defaultValue: [],
  });

  function addComments(comment) {
    setComments((oldComments) => [
      ...oldComments,
      {
        ...comment,
        id: crypto.randomUUID(),
      },
    ]);
  }

  // function handleCheckbox(comment){
  //   {if ({comment.movieNo}){
  //     <p>`${comment.movieNo}` üò® Do it!</p>
  //   } else({comment.movieYes}) {
  //     <p>`${comment.movieYes}` üòç Hope you liked it!</p>
  //   }}
  // }

  return (
    <>
      <GlobalStyle />
      <main>
        <Header title="This is my universe" heading="Welcome to my universe" />
        <About aboutImageSrc={ImageAlienInvasion} aboutAlt="Alien Invasion" />
        <Projects />
        <Contact />
        {/* <Form onSubmit={addComments} onClick={handleCheckbox} /> */}
        <CommentList comments={comments} />
      </main>
      <Footer />
    </>
  );
}

export default App;
