// import styled from "styled-components";

export default function Form({ onSubmit, labelAnimals }) {
  function handleSubmit(event) {
    event.preventDefault();

    const formData = new FormData(event.target);
    const data = Object.fromEntries(formData);

    console.log(data);
    onSubmit(data);

    event.target.reset();
    event.target.elements.name.focus();
  }

  return (
    <form onSubmit={handleSubmit}>
      <fieldset>
        <legend>Write me</legend>
        <FormInput labelFor="nameInput" type="text" placeholder="Peppa">
          First name:{" "}
        </FormInput>
        <label htmlFor="textarea">Comment: </label>
        <textarea
          name="textarea"
          type="text"
          placeholder="Like your projects!"
        ></textarea>
        <label htmlFor="animals">{labelAnimals}</label>
        <select name="animals">
          <FormSelectOptions value="dog">üê∂</FormSelectOptions>
          <FormSelectOptions value="cat">üê±</FormSelectOptions>
          <FormSelectOptions value="mouse">üê≠</FormSelectOptions>
          <FormSelectOptions value="hamster">üêπ</FormSelectOptions>
          <FormSelectOptions value="rabbit">üê∞</FormSelectOptions>
          <FormSelectOptions value="cow">üêÆ</FormSelectOptions>
          <FormSelectOptions value="pig">üê∑</FormSelectOptions>
        </select>
        <button type="submit">Send</button>
      </fieldset>
    </form>
  );
}

function FormInput({ labelFor, children, type, placeholder }) {
  return (
    <>
      <label htmlFor={labelFor}>{children}</label>
      <input
        name={labelFor}
        id={labelFor}
        type={type}
        placeholder={placeholder}
        required
      ></input>
    </>
  );
}

function FormSelectOptions({ value, children }) {
  return (
    <option value={value} id={value}>
      {children}
    </option>
  );
}
