  "🦨",
  "🦡",
  "🦦",
  "🦔",
  "🐻‍❄️",
];

export default function Form({ onSubmit, handleRadioChange, checked }) {
  function handleSubmit(event) {
    event.preventDefault();

    const formData = new FormData(event.target);
    const data = Object.fromEntries(formData);

    console.log(data);
    onSubmit(data);

    event.target.reset();
  }

  //   function handleRadioChange(event) {
  //     if (event.checked) {
  //       console.log(event.target.value);
  //     }
  //     console.log(event.target.value);
  //   }

  function handleNameChange(event) {
    console.log(event.target.value);
  }

  return (
    <form onSubmit={handleSubmit}>
      <fieldset>
        <legend>Write me</legend>
        <FormInput
          handleNameChange={handleNameChange}
          labelFor="name"
          type="text"
          placeholder="Peppa"
        >
          First name:
        </FormInput>
        <label htmlFor="textarea">Comment: </label>
        <textarea
          name="textarea"
          type="text"
          placeholder="Like your projects!"
        ></textarea>
        <label htmlFor="animals">Whats your favorite animal?</label>
        <select name="animals">
          {animalEmojis.map((animalEmoji) => (
            <option key={animalEmoji} value={animalEmoji} />
          ))}
        </select>
        <fieldset>
          <legend>Did you watch "Spider-Man: A new universe"?</legend>
          <input
            name="checkbox"
            type="radio"
            id="movieNo"
            value="No"
            checked={checked}
            onChange={handleRadioChange}
          ></input>
          <label htmlFor="movieNo">No</label>
          <input
            name="checkbox"
            type="radio"
            id="movieYes"
            // checked={checked}
            // onChange={handleRadioChange}
          ></input>
          <label htmlFor="movieYes">Yes</label>
        </fieldset>
        <button type="submit">Send</button>
      </fieldset>
    </form>
  );
}

function FormInput({
  handleNameChange,
  labelFor,
  children,
  type,
  placeholder,
}) {
  return (
    <>
      <label htmlFor={labelFor}>{children}</label>
      <input
        onChange={handleNameChange}
        name={labelFor}
        id={crypto.randomUUID()}
        type={type}
        placeholder={placeholder}
        required
      ></input>
    </>
  );
}

